import React, { useState } from 'react';

const plans = [
  {
    tag: "For one person",
    title: "Free Consultation",
    description: "Please select the date. We will get back to you",
    buttonText: "Book Now",
    buttonSecondary: false,
    showDateInput: true, // ðŸ‘ˆ only show for this card
  },
  {
    tag: "For teams",
    title: "Premium Plan",
    description: "Best for companies with multiple hiring needs",
    buttonText: "Contact Sales",
    buttonSecondary: true,
    showDateInput: false,
  },
];

function CanvaStylePricing() {
  const [selectedDates, setSelectedDates] = useState({});

  const handleDateChange = (index, date) => {
    setSelectedDates(prev => ({
      ...prev,
      [index]: date,
    }));
  };

  const handleBook = (index) => {
    const date = selectedDates[index];
    if (plans[index].showDateInput && !date) {
      alert("Please select a date before booking.");
      return;
    }
    alert(`Booking confirmed${date ? ` on ${date}` : ''} for plan: ${plans[index].title}`);
    // You can also send the data to backend here
  };

  return (
    <div className="pricing-section" style={{ padding: '20px' }}>
      <h2 style={{ textAlign: 'center' }}>Our Plans</h2>
      <div style={{ display: 'flex', gap: '20px', justifyContent: 'center', flexWrap: 'wrap' }}>
        {plans.map((plan, index) => (
          <div
            key={index}
            style={{
              border: '1px solid #ddd',
              borderRadius: '10px',
              padding: '20px',
              width: '300px',
              boxShadow: '0 4px 12px rgba(0,0,0,0.1)',
            }}
          >
            <div style={{ fontSize: '14px', color: '#888' }}>{plan.tag}</div>
            <h3 style={{ margin: '10px 0' }}>{plan.title}</h3>
            <p>{plan.description}</p>

            {plan.showDateInput && (
              <div style={{ marginTop: '10px' }}>
                <label style={{ fontSize: '14px' }}>Select Date:</label>
                <input
                  type="date"
                  value={selectedDates[index] || ''}
                  onChange={(e) => handleDateChange(index, e.target.value)}
                  style={{ display: 'block', marginTop: '5px', padding: '5px', width: '100%' }}
                />
              </div>
            )}

            <button
              style={{
                padding: '10px 15px',
                marginTop: '15px',
                backgroundColor: plan.buttonSecondary ? '#eee' : '#007bff',
                color: plan.buttonSecondary ? '#333' : '#fff',
                border: 'none',
                borderRadius: '5px',
                cursor: 'pointer',
                width: '100%',
              }}
              onClick={() => handleBook(index)}
            >
              {plan.buttonText}
            </button>
          </div>
        ))}
      </div>
    </div>
  );
}

export default CanvaStylePricing;
