import React from 'react';
import PricingCard from './PricingCard';
import './PricingCard.css';

const plans = [
  {
    tag: "For one person",
       title: "Free Consultation",
    description: "Please select the date.We will get back to you",
  
    buttonText: "Get ITWALKIN Pro",
    buttonSecondary: false,
  },
  {
    tag: "For your team",
    offer: "Save 33%",
    title: "Canva Teams",
    description: "Transform teamwork, grow your brand, and simplify workflows.",
    price: "₹4,000",
    subtext: "/12 months per person",
    linkText: "Custom  •  People",
    buttonText: "Get Canva Teams",
    buttonSecondary: true,
    extraButton: "Contact Sales",
  },
  {
    tag: "For your organisation",
    offer: null,
    title: "Canva Enterprise",
    description: "Empower your organisation with an all-in-one workplace solution.",
    price: "Let’s talk",
    subtext: "Get in touch to learn more",
    buttonText: "Contact Sales",
    buttonSecondary: true,
    extraButton: "Book a demo",
  }
];

const CanvaStylePricing = () => {
  return (
    <div className="pricing-wrapper">
      {plans.map((plan, index) => (
        <PricingCard key={index} {...plan} />
      ))}
    </div>
  );
};

export default CanvaStylePricing;
